import random
import csv
from jellyfish import soundex
from operator import itemgetter
from itertools import groupby
from g2p_en import G2p



def getSoundexDict(dList):
    return sorted(dict_.items(), key = itemgetter(1))


with open('CMU') as f:
    lines = f.readlines()

all_words = set()
g2p = G2p()
threshold = .90


for line in lines:
    if line[0] == ";":
        continue
    word,phenomes = line.strip().split("  ")
    if "(" in word or "'" in word or "{" in word or "}" in word or "." in word:
        continue
    all_words.add(word)

words = list(all_words)
res = [soundex(x) for x in words]    # to get the val for each elem
dict_ = dict(list(zip(words, res)))  # dict_ with k,v as name/val

grouped = [list(g) for _, g in groupby(getSoundexDict(words), lambda x: x[1])]

close_matches = []

for g in grouped:
    if len(g) > 1:
        main = g[0][0]
        main_phonemes = tuple(g2p(main))
        for w in g[1:]:
            comparing = tuple(g2p(w[0]))
            if len(comparing) == len(main_phonemes):
                total = 0
                divisor = len(main_phonemes)
                for i in range(len(main_phonemes)):
                    #print(cols.index(comparing[i]))
                    #print(main_phonemes, comparing, main, w[0])
                    total += phoneme_sim[main_phonemes[i]][cols.index(comparing[i])]
                if (val := total/divisor) > threshold:
                    #print(comparing, main_phonemes, main, w[0], round(val,3))
                    close_matches.append((main_phonemes, comparing, main, w[0], round(val,4)))

print(len(close_matches))



non_matches = []
bad_threshold = 0.8
for _ in range(len(words)):
    total = 0
    word1 = random.choice(words)
    word2 = random.choice(words)
    while word1 == word2: #highly highly unlikely to ever hit this.
        print("that's amazing", word1) #if this gets printed itll be hype.
        word2 = random.choice(words)
    p1,p2 = tuple(g2p(word1)), tuple(g2p(word2))
    if ' ' in p1 or ' ' in p2:
        continue
    divisor = max(len(p1), len(p2))
    for i in range(divisor):
        if i >= len(p2) or i >= len(p1):
            total += 0
            continue
        total += phoneme_sim[p1[i]][cols.index(p2[i])]
    if (val:= total/divisor) < bad_threshold:
        #print(word1, word2, round(val,3))
        non_matches.append((p1,p2,word1,word2,round(val,4)))


with open('data.csv', 'w', newline='') as file:
    writer = csv.writer(file)
    writer.writerow(["phoneme1", "phoneme2", "word1","word2", "similarity", "similar"])
    for match in close_matches:
        writer.writerow([match[0],match[1],match[2],match[3],match[4], "1"])
    for match in non_matches:
        writer.writerow([match[0],match[1],match[2],match[3],match[4], "0"])



